from bs4 import BeautifulSoup
from markdownify import markdownify as md

def html_to_text_markdown(html_content: str) -> str:
    # parse the HTML content
    soup = BeautifulSoup(html_content, "html.parser")
    
    # Remove all image tags completely
    for img in soup.find_all("img"):
        img.decompose()
    
    # For links, you might want to keep their inner text but remove the <a> tag.
    # Replace each <a> tag with its text.
    for a in soup.find_all("a"):
        a.replace_with(a.get_text())
    
    # Now convert the cleaned HTML to markdown. You can set heading_style to 'ATX'
    # so that headings use # instead of underlines.
    markdown_text = md(str(soup), heading_style="ATX")
    return markdown_text

